DROP TABLE IF EXISTS park;
CREATE TABLE IF NOT EXISTS park(
   parkId           VARCHAR(11) NOT NULL PRIMARY KEY
  ,parkName         VARCHAR(25) NOT NULL
  ,parkType         VARCHAR(5) DEFAULT NULL
  ,parkDoroAddress  VARCHAR(37) DEFAULT NULL
  ,parkJibunAddress VARCHAR(27) DEFAULT NULL
  ,dong VARCHAR(10) DEFAULT NULL
  ,parkLat          NUMERIC(11,8) NOT NULL
  ,parkLng          NUMERIC(11,7) NOT NULL
  ,parkArea         NUMERIC(9,2) NOT NULL
  ,공원보유시설운동시설       VARCHAR(48) DEFAULT NULL
  ,공원보유시설유희시설       VARCHAR(34) DEFAULT NULL
  ,공원보유시설편익시설       VARCHAR(7) DEFAULT NULL
  ,공원보유시설교양시설       VARCHAR(8) DEFAULT NULL
  ,공원보유시설기타시설       VARCHAR(2)  DEFAULT NULL
  ,notificationDate DATE DEFAULT NULL
  ,parkAuthority    VARCHAR(9) DEFAULT NULL
  ,parkPhonNumber   VARCHAR(12) DEFAULT NULL
  ,parkDate         DATE  DEFAULT NULL
);

